class P1{
	public static void check(int n) {
		if(n==2) {
			System.out.println("Prime number");
		}else if(n%2!=0) {
			System.out.println("Prime Number");
		}
		else {
			System.out.println("Not prime number");
		}
	}
}
public class Prime_num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		P1.check(25);
	}

}
